famille(1).femme(titi).homme("toto").parent(X,Y) :- enfant(Y,X).pere(X) :- parent(X, Y), homme(X).famille(Y ) :- nbfamille(Z), famille(X), Y = X + 1, Y <= Z.famille(Y) :- Y, Y >= 5 - (Y * 2).%Il est posible de melanger des variables avec des constantes pour un meme predicat comme par exemple ::- marie(X,titi), femme(X).:- toto(X,fifi), tutu(toto).pere(X,Y) :- parent(X,Y), not femme(X).%Les prédicats de cette forme sont géré par le parser, cependant l'exportation ne prend pas correctement en chargefamille(femme(tom), Y) :- fille(femme(Julie), 5), chaise(Julie), chose(Y).charel(X, Machin) :- (toto(X) ; tutu(Machin)), (charles(Machin) ; charles(X)).